mapper.select:
SELECT s.*
FROM staff s
         JOIN code_department cd ON s.department_code = cd.department_code
WHERE s.staff_name = #{staffName}
  AND cd.department_name = #{departmentCode}

mapper.update:
UPDATE STAFF
    SET
        staff_name = #{staffName},
    jumin_no=#{juminNo},
    department_code = (
    SELECT department_code
    FROM code_department
    WHERE department_name = #{departmentCode}
    ),
    school_code = (
        SELECT school_code
        FROM code_school
        WHERE school_name = #{schoolCode}
    ),
    graduate_day = #{graduateDay}
WHERE staff_no = #{staffNo}

mapper.select:
SELECT * FROM user WHERE 1=1

    AND name LIKE CONCAT('%', #{name}, '%')


    AND department = #{department}
